const renderTokenAndText = (acc, { token, text }, index) => {
  return (acc +=
    text === '\n'
      ? '<br>'
      : `<span class="token">${token}</span><code style="background: #${
          COLOURS[index % COLOURS.length]
        }66">${text.replace(/&/g, '&amp;').replace(/</g, '&lt;').replace(/>/g, '&gt;')}</code>`)
}
